/**
 * Allows resolving element value.
 */
export class FormHelper {

    /**
     * Checks if the element is an "input".
     * @param {Object} element 
     * @return {boolean} 
     */
    static isInput(element) {
        return element.tagName === 'INPUT';
    }

    /**
     * Checks if the element is a "textarea".
     * @param {Object} element 
     * @return {boolean} 
     */
    static isTextarea(element) {
        return element.tagName === 'TEXTAREA';
    }

    /**
     * Checks if the element is a "select" element.
     * @param {Object} element 
     * @return {boolean} 
     */
    static isSelect(element) {
        return element.tagName === 'SELECT';
    }

    /**
        * Checks if the element is an input with the type "checkbox".
        * @param {Object} element 
        * @return {boolean} 
        */
    static isCheckbox(element) {
        return element.type && element.type === 'checkbox';
    }

    /**
        * Checks if the element is an input with the type "radio".
        * @param {Object} element 
        * @return {boolean} 
        */
    static isRadio(element) {
        return element.type && element.type === 'radio';
    }

    /**
        * Checks if the element is an input with the type "number" or "range".
        * @param {Object} element 
        * @return {boolean} 
        */
    isNumberElement(element) {
        return element.type && (element.type === 'number' || element.type === 'range');
    }

    /**
     * Returns cheched or the checbox value.
     * @param {Object} checkbox
     * @return {string|boolean} 
     */
    static GetCheckboxValue(checkbox) {
        return checkbox.value !== 'on' ? checkbox.value : checkbox.checked;
    }

    /**
     * Returns the input value.
     * @param {Object} element
     * @return {string} 
     */
    static GetInputValue(element) {
        if (FormHelper.isCheckbox(element)) {
            return FormHelper.GetCheckboxValue(element);
        } else {
            return element.value;
        }
    }

    /**
     * Returns the selected value or selected values (multiple).
     * @param {Object} select
     * @return {string|Array} 
     */
    static GetSelectValue(select) {
        if (select.multiple) {
            let values = [];
            let options = select.options;
            for (let i = 0; i < options.length; i++) {
                let option = options[i];
                if (option.selected) {
                    values.push(option.value);
                }
            }
            return values;
        }
        else {
            return select.options[select.selectedIndex].value;
        }
    }

    /**
     * Check the element type (input, checbox, select, textarea) and returns the value.
     * @param {Object} element
     * @return {Object|Array|string|boolean} 
     */
    static getElementValue(element) {
        if (FormHelper.isInput(element)) {
            return FormHelper.GetInputValue(element);
        } else if (FormHelper.isTextarea(element)) {
            return element.value;
        } else if (FormHelper.isSelect(element)) {
            return FormHelper.GetSelectValue(element);
        }
    }

    static hasClassName(className, classNameToCheck) {
        return className.indexOf(classNameToCheck) != -1;
    }

    static addClassName(className, classNameToAdd) {
        return className + ' ' + classNameToAdd;
    }

    static removeClassName(className, classNameToRemove) {
        return className.replace(classNameToRemove, '');
    }
}

import { isObject, isArray } from '../common/util';

/**
 * Extends the target object with the source object.
 * @param {Object} source 
 * @param {Object} target 
 * @return {Object} The target object.
 */
export function extend(source, target) {
    if (typeof source === 'object') {
        for (let name in source) {
            if (!target.hasOwnProperty(name)) {
                let sourceValue = source[name];
                if (isArray(sourceValue)) {
                    target[name] = sourceValue.slice(0);
                }
                else if (isObject(sourceValue)) {
                    target[name] = extend(sourceValue, {});
                }
                else {
                    target[name] = sourceValue;
                }
            }
        }
    }
    return target;
}

/**
 * copies an object.
 * @param {Object} obj 
 */
export function clone(obj) {
    return extend(obj, {});
}

/**
 * Returns the source object without the omitted names.
 * @param {Object} obj 
 * @param {Array} names
 */
export function omit(obj, names = []) {
    let result = {};
    for (let name in obj) {
        if (obj.hasOwnProperty(name) && names.indexOf(name) === -1) {
            result[name] = obj[name];
        }
    }
    return result;
}

import { isObject } from '../common/util';

/**
 * Allows validating a value, a property or a form model with validations (required, minlength, maxlength, pattern, email, custom).
 * 
 * @example
 * let model = {
 *   firstname: '',
 *   lastname: ''
* };
*
* let validations = {
 *  firstname: [required('Firstname required')],
 *  lastname: [required('Lastname required')]
* };
*
* let value = model['lastname'];
*
* let error = ValidationHelper.validateValue(model,value,validations);
* // error => 'Lastname required'
 * 
 */
export class ValidationHelper {

    /**
     * Checks the value with the validations (pass the model to the custom validations) and returns the first error message.
     * @param {Object} model 
     * @param {string|number|boolean} value 
     * @param {Array} validations 
     * @return {string|undefined} The first error message or undefined.
     */
    static validateValue(model, value, validations = []) {
        for (let i = 0; i < validations.length; i++) {
            let validator = validations[i];
            let error = validator(value, model);
            if (error) {
                return error;
            }
        }
    }

    /**
     * Checks the property name with the validations (pass the model to the custom validations) and returns the first error message.
     * 
     * @example
     * let error = ValidationHelper.validateProperty(model, 'lastname', validations);
     * // error => 'Lastname required'
     * 
     * @param {Object} model 
     * @param {string|number|boolean} name 
     * @param {Array} validations 
     * @return {string|undefined} The first error message or undefined.
     */
    static validateProperty(model, name, validations = []) {
        let value = model[name];
        return ValidationHelper.validateValue(model, value, validations);
    }

    /**
     * Checks if the validations object has validations for the name
     * 
     * @example
     * let errors = ValidationHelper.validateAll(model,validations);
     * // errors => {firstname: 'Firstname required', lastname: 'Lastname required'}
     * @param {string} name 
     * @param {Object} validations 
     * @return {boolean}
     */
    static hasValidations(name, validations) {
        return validations.hasOwnProperty(name);
    }

    /**
     * Returns the length of the errors object.
     * @param {Object} errors 
     * @return {number}
     */
    static countErrors(errors) {
        return Object.keys(errors).length;
    }

    /**
     * Checks if the object is empty.
     * @param {Object} errors 
     * @return {boolean}
     */
    static hasErrors(errors) {
        return isObject(errors) && ValidationHelper.countErrors(errors) > 0;
    }

    /**
     * Validates the form model and returns an object with the errors messages.
     * @param {Object} model 
     * @param {Object} validations 
     * @return {Object}
     */
    static validateAll(model, validations = {}) {
        let errors = {};

        for (let name in validations) {
            if (ValidationHelper.hasValidations(name, validations)) {
                let fieldValidations = validations[name];
                if (!Array.isArray(fieldValidations)) { throw new Error('Validations require an Array'); }

                let error = ValidationHelper.validateProperty(model, name, fieldValidations);
                if (error) {
                    errors[name] = error;
                }
            }
        }

        return errors;
    }
}

